import axios from 'axios';

const api = axios.create({
    baseURL: 'http://localhost:3001',
  });

export const requestMeals = async () => {
    const { data } = api.get('/meals');
    return data
}

export const requestDrinks = async () => {
    const { data } = api.get('/drinks');
    return data
}
