import React from 'react';
/* import blackHeart from '../images/blackHeartIcon.svg';
import whiteHeart from '../images/whiteHeartIcon.svg'; */
import emptyheart from '../images/emptyheart.png';
import fullheart from '../images/fullheart.jpg';

const isFavoritedButton = (favorite) => {
  if (favorite === false) {
    return (<img src={ emptyheart } alt="white heart" data-testid="favorite-btn" />);
  } if (favorite === true) {
    return (<img src={ fullheart } alt="black heart" data-testid="favorite-btn" />);
  }
};

export default isFavoritedButton;
